form.script:
    $(document).ready(function(){
    var xhr;
    var state_id, $state_id;
    var kota_id, $kota_id;
    var kecamatan_id, $kecamatan_id;
    var kelurahan_id, $kelurahan_id;

    $country_id = $('#country_id').selectize({
        valueField: 'id',
        labelField: 'name',
        searchField: 'name',
        options: [{"id":"", "name":""}],
        create: false,
        persist: true,

        load: function(query, callback) {
            if (!query.length) return callback();
            $.ajax({
                url: '/selectize/countries',
                type: 'GET',
                dataType: 'json',
                data: {
                    q: query,
                    page_limit: 10,
                },
                error: function(res) {
                    console.log(res);
                },
                success: function(res) {
                    callback(res);
                }
            });
        },
        onChange: function(value) {
            if (!value.length) return;
            state_id.disable();
            state_id.clearOptions();

            state_id.load(function(callback) {
                xhr && xhr.abort();
                xhr = $.ajax({
                    type: 'GET',
                    dataType: 'json',
                    url: '/selectize/states/' + value ,
                    success: function(results) {
                        console.log(results);
                        state_id.enable();
                        callback(results);
                        state_id.setValue();

                    },
                    error: function() {
                        callback();
                    }
                })
            });
        }
    });

    $state_id = $('#state_id').selectize({
        valueField: 'id',
        labelField: 'name',
        searchField: 'name',
        options: [],
        create: false,
        onChange: function(value) {
            if (!value.length) return;
            kota_id.load(function(callback) {
                xhr && xhr.abort();
                xhr = $.ajax({
                    type: 'GET',
                    dataType: 'json',
                    url: '/selectize/cities/' + value ,
                    success: function(results) {
                        kota_id.disable();
                        kota_id.clearOptions();
                        kota_id.enable();
                        callback(results);
                        kota_id.setValue();
                    },
                    error: function() {
                        callback();
                    }
                })
            });

        }
    });

    $kota_id = $('#kota_id').selectize({
        valueField: 'id',
        labelField: 'name',
        searchField: ['name'],
        onChange: function(value) {
            if (!value.length) return;

            $("#city").val(kota_id.options[value].name);

            kecamatan_id.disable();
            kecamatan_id.clearOptions();

            kecamatan_id.load(function(callback) {
                xhr && xhr.abort();
                xhr = $.ajax({
                    type: 'GET',
                    dataType: 'json',
                    url: '/selectize/kecamatans/' + value ,
                    success: function(results) {
                        kecamatan_id.enable();
                        callback(results);
                        kecamatan_id.setValue();
                    },
                    error: function() {
                        callback();
                    }
                })
            });

        }
    });
    $kecamatan_id = $('#kecamatan_id').selectize({
        valueField: 'id',
        labelField: 'name',
        searchField: ['name'],
        onChange: function(value) {
            if (!value.length) return;
            kelurahan_id.disable();
            kelurahan_id.clearOptions();
            kelurahan_id.load(function(callback) {
                xhr && xhr.abort();
                xhr = $.ajax({
                    type: 'GET',
                    dataType: 'json',
                    url: '/selectize/kelurahans/' + value ,
                    success: function(results) {
                        kelurahan_id.enable();
                        callback(results);
                        kelurahan_id.setValue();
                    },
                    error: function() {
                        callback();
                    }
                })
            });

        }
    });

    $kelurahan_id = $('#kelurahan_id').selectize({
        valueField: 'id',
        labelField: 'name',
        searchField: ['name'],
    });

    country_id = $country_id[0].selectize;

    state_id = $state_id[0].selectize;
    kota_id  = $kota_id[0].selectize;
    kecamatan_id  = $kecamatan_id[0].selectize;
    kelurahan_id  = $kelurahan_id[0].selectize;

    country_id.setValue();

});
